package demo;

public class Day3Pro4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int age = 10;
        double ages = age;
        System.out.println("After converting int to double: " + ages);

        double num = 45.67;
        int nums = (int) num;
        System.out.println("After converting double to int: " + nums);

        char ch = 'A';
        int val = (int) ch;
        System.out.println("After converting char to int: " + val);

        int val1 = 65;
        char ch1 = (char) val1;
        System.out.println("After converting int to char: " + ch1);
	}

}
